import { useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Textarea from './components/Textarea';



function App() {

const [mode,setMode]=useState('dark');

  const toggle=()=>{
    if(mode === 'light'){
      setMode('dark');
    }
    else{
      setMode('light');
    }
  }

  return (
    //text ko change karenge without loading the page
    <>
    <Navbar title="CONVERTER" mode={mode} toggleMode={toggle} />


    <div class='container'> <Textarea/> </div>

    </>
  );
}

export default App;
