import React, { useState } from 'react'

export default function Textarea(props) {
/*button*/ 
   const Clickonclick= () =>{
    let newText = text.toUpperCase();
    setText(newText)
   } 

   const Clickonclick2= () =>{
    let newText = text.toLowerCase();
    setText(newText)
   } 


   const ClearText= () =>{
    let newText = "";
    setText(newText)
   } 
   


   const gmail= () =>{
    let newText = text+"@gmail.com";
    setText(newText)
   } 
    /*textarea*/
    const Changeonchange = (event) =>{
        setText(event.target.value)
    }


    //states
    const [text,setText]=useState('');


//buttons-dark/light mode 
const[btntext,setbtntext]=useState("Enable Dark Mode")


    //dark mode- style
  /*  const [myStyle,setstyle]=useState({
        color: 'black',
        backgroundColor: 'white'
    })


  /*  const Dark = () =>{
        if(myStyle.color == 'white'){
            setstyle({
                color: 'black',
                backgroundColor: 'white'
            })
            setbtntext("Enable Dark Mode")
        }
        else{
            setstyle({
                color: 'white',
                backgroundColor: 'black'
            })
            setbtntext("Enable Light Mode")
        }
    }*/




  return (
    <>
 <form style={{backgroundColor: props.mode==='light'?'dark':'light'}} >

  <div class="form-group">
    <label for="exampleFormControlTextarea1">Lower-Upper/ Upper-Lower</label>
    <textarea class="form-control" value={text} style={{backgroundColor: props.mode==='light'?'dark':'light'}} onChange={Changeonchange} id="exampleFormControlTextarea1" rows="8" ></textarea>
  </div>

  <br/>
  <div classname="col-auto">
    <button type="button" onClick={Clickonclick} classname="btn btn-info mx-2">Convert to Upper Case</button>
    <button type="button" onClick={Clickonclick2} classname="btn btn-info mx-2">Convert to Lower Case</button>
    <button type="button" onClick={ClearText} classname="btn btn-info mx-2">Clear Text</button>
    <button type="button" onClick={gmail} classname="btn btn-info mx-2">Create Gmail I'd</button>
   
  </div>
  <br/>

    <h1> TEXT SUMMARY: </h1>
    {text.split(" ").length} words , {text.length} characters , {text.split(" ").length*0.008} sec Reading Time <br/>
    <h7>Preview: <br/> {text}</h7>
   


</form>

</>
  )
}
