import React, { useState } from 'react'

export default function Textarea() {
/*button*/ 
   const Clickonclick= () =>{
    let newText = text.toUpperCase();
    setText(newText)
   } 
   
    /*textarea*/
    const Changeonchange = (event) =>{
        setText(event.target.value)
    }


    //states
    const [text,setText]=useState('TYPE');
  return (
    <>
<form classname="row gy-2 gx-3 align-items-center">
  <div classname="col-auto">
    <label classname="visually-hidden" for="autoSizingInput">Name</label>
    <br/><input type="text" classname="form-control" id="autoSizingInput" placeholder="Jane Doe"/>
  </div>
  <br/>
  <div classname="col-auto">
    <label classname="visually-hidden" for="autoSizingInputGroup">Username</label>
    <div classname="input-group">
      <input type="text" classname="form-control" id="autoSizingInputGroup" placeholder="Username"/>
    </div>
  </div>
  <br/>


  <div class="form-group">
    <label for="exampleFormControlTextarea1">Lower case to Upper case</label>
    <textarea class="form-control" value={text} onChange={Changeonchange} id="exampleFormControlTextarea1" rows="3" ></textarea>
  </div>

  <br/>
  <div classname="col-auto">
    <button type="button" onClick={Clickonclick} classname="btn btn-primary">Convert</button>
  </div>
</form>
</>
  )
}
