import { useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Textarea from './components/Textarea';
import Alert from './components/Alert';


function App() {

const [mode,setMode]=useState('light');
const [alert,setAlert]=useState(null);

  const toggle=()=>{
    if(mode == 'light'){
      setMode('dark');
      document.body.style.background='#0E2046';
    }
    else{
      setMode('light');
      document.body.style.background='white';
    }
  }



  const showalert=(message,type)=>{
    msg=message;
    type=type;
    setAlert;

  }

  return (
    //text ko change karenge without loading the page
    <>
    <Navbar title="CONVERTER" mode={mode} toggle={toggle} />
    <Alert alert='WELCOME! START TYPING!'/>

    <div class='container'> <Textarea mode={mode} /> </div>

    </>
  );
}

export default App;
