import React, { useState } from 'react'

export default function Textarea() {
/*button*/ 
   const Clickonclick= () =>{
    let newText = text.toUpperCase();
    setText(newText)
   } 

   const Clickonclick2= () =>{
    let newText = text.toLowerCase();
    setText(newText)
   } 
   
    /*textarea*/
    const Changeonchange = (event) =>{
        setText(event.target.value)
    }


    //states
    const [text,setText]=useState('');
  return (
    <>
    <div div classname="container">
<form classname="row gy-2 gx-3 align-items-center">
  <div classname="col-auto">
    <label classname="visually-hidden" for="autoSizingInput">Name</label>
    <br/><input type="text" classname="form-control" id="autoSizingInput" placeholder="Naman"/>
  </div>
  <br/>


  <div class="form-group">
    <label for="exampleFormControlTextarea1">Lower-Upper/ Upper-Lower</label>
    <textarea class="form-control" value={text} onChange={Changeonchange} id="exampleFormControlTextarea1" rows="8" ></textarea>
  </div>

  <br/>
  <div classname="col-auto">
    <button type="button" onClick={Clickonclick} classname="btn btn-primary mx-2">Convert to Upper Case</button>
    <button type="button" onClick={Clickonclick2} classname="btn btn-primary mx-2">Convert to Lower Case</button>
    <button type="button" onClick={Clickonclick2} classname="btn btn-primary mx-2">Cler Text</button>
  
  </div>
</form>
</div>
<br/>
<div classname="container ">
    <h1> TEXT SUMMARY: </h1>
    {text.split(" ").length} words , {text.length} characters , {text.split(" ").length*0.008} sec Reading Time <br/>
    <h7>Preview: <br/> {text}</h7>
   
</div>


</>
  )
}
