import React, { useState } from 'react'

export default function Textarea() {
/*button*/ 
   const Clickonclick= () =>{
    let newText = text.toUpperCase();
    setText(newText)
   } 

   const Clickonclick2= () =>{
    let newText = text.toLowerCase();
    setText(newText)
   } 


   const ClearText= () =>{
    let newText = "";
    setText(newText)
   } 
   


   const gmail= () =>{
    let newText = text+"@gmail.com";
    setText(newText)
   } 
    /*textarea*/
    const Changeonchange = (event) =>{
        setText(event.target.value)
    }


    //states
    const [text,setText]=useState('');



    //dark mode- style
    let myStyle={
        color: 'red',
        backgroundColor: 'black'
    }




  return (
    <>
    < div classname="container"  style={myStyle}>
<form classname="row gy-2 gx-3 align-items-center">
  
  <br/>


  <div class="form-group">
    <label for="exampleFormControlTextarea1">Lower-Upper/ Upper-Lower</label>
    <textarea class="form-control" value={text} onChange={Changeonchange} id="exampleFormControlTextarea1" rows="8" ></textarea>
  </div>

  <br/>
  <div classname="col-auto">
    <button type="button" onClick={Clickonclick} classname="btn btn-info mx-2">Convert to Upper Case</button>
    <button type="button" onClick={Clickonclick2} classname="btn btn-info mx-2">Convert to Lower Case</button>
    <button type="button" onClick={ClearText} classname="btn btn-info mx-2">Clear Text</button>
    <button type="button" onClick={gmail} classname="btn btn-info mx-2">Create Gmail I'd</button>
    <button type="button" onClick={gmail} classname="btn btn-info mx-2">Dark Mode</button>
  
  </div>
</form>
<br/>

    <h1> TEXT SUMMARY: </h1>
    {text.split(" ").length} words , {text.length} characters , {text.split(" ").length*0.008} sec Reading Time <br/>
    <h7>Preview: <br/> {text}</h7>
   
</div>


</>
  )
}
