import React, { useState } from 'react'

export default function Navbar(props) {
 




  
/*red*/
const [r, setred]=useState('light'); 
const redmode =()=>{
 if(r==='light'){
   setgreen('dark');
   document.body.style.backgroundColor='red';
   document.body.style.Color='white';
   props.showalert("RED MODE ENABLED!","SUCCESS");
 }
 else{
   setgreen('light');
   document.body.style.backgroundColor='white';
 }
}


/*yellow*/
const [y, setyellow]=useState('light'); 
const yellownmode =()=>{
 if(y==='light'){
   setgreen('dark');
   document.body.style.backgroundColor='yellow';
   props.showalert("YELLOW MODE ENABLED!","SUCCESS");
 }
 else{
   setgreen('light');
   document.body.style.backgroundColor='white';
 }
}



/*<button type="button" onClick={greenmode} className="btn btn-outline-success">Green Mode</button>
<button type="button" onClick={redmode}className="btn btn-outline-danger">Red Mode</button>
<button type="button" onClick={yellownmode}className="btn btn-outline-warning">Yellow Mode</button>*/

  return (
    <nav className={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>
  <div className="container-fluid">
    <a className="navbar-brand" href="#">{props.title}</a>
      




<div className={`form-check form-switch text-${props.mode==='light'?'dark':'light'}`}>
<input className="form-check-input" onClick={props.toggle} type="checkbox" id="flexSwitchCheckDefault"/>
<label className="form-check-label"  htmlFor="flexSwitchCheckDefault">Dark/Light Mode</label> 
</div>




<div className={`form-check form-switch text-${props.mode==='light'?'yellow':'light'}`}>
<input className="form-check-input" onClick={toggle1} type="checkbox" id="flexSwitchCheckDefault"/>
<label className="form-check-label"  htmlFor="flexSwitchCheckDefault">Yellow Mode</label> 
</div>




<div className={`form-check form-switch text-${props.mode==='light'?'Red':'light'}`}>
<input className="form-check-input" onClick={props.toggle} type="checkbox" id="flexSwitchCheckDefault"/>
<label className="form-check-label"  htmlFor="flexSwitchCheckDefault">Red Mode</label> 
</div>
  </div>
</nav>
  )
}
