import { useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Textarea from './components/Textarea';
import Alert from './components/Alert';


function App() {

const [mode,setMode]=useState('light');

const [alert,setAlert]=useState(null);

  const showalert= (message,type) => {
    setAlert({
      msg:message,
    type:type
    })

  }

  const toggle=()=>{
    if(mode == 'light'){
      setMode('dark');
      document.body.style.background='#0E2046';
      showalert("DARK MODE ENABLED","SUCESS!");
    }
    else{
      setMode('light');
      document.body.style.background='white';
      showalert("LIGHT MODE ENABLED","SUCESS!");
    }
  }




  return (
    //text ko change karenge without loading the page
    <>
    <Navbar title="CONVERTER" mode={mode} toggle={toggle} />
    <Alert alert={showalert} />

    <div className='container'> <Textarea mode={mode} /> </div>

    </>
  );
}

export default App;
