  const toggle=()=>{
    if(mode == 'light'){
      setMode('dark');
      document.body.style.background='grey';
      document.body.sty
    }
    else{
      setMode('light');
      document.body.style.background='white';
    }
  }

  return (
    //text ko change karenge without loading the page
